#!/usr/bin/env node
'use strict';
/**
 * @fileoverview Script to launch a clean Chrome instance on-demand.
 *
 * Assuming Lighthouse is installed globally or `npm link`ed, use via:
 *     chrome-debug
 * Optionally enable extensions or pass a port, additional chrome flags, and/or a URL
 *     chrome-debug --port=9222
 *     chrome-debug http://goat.com
 *     chrome-debug --show-paint-rects
 *     chrome-debug --enable-extensions
 */
require('./compiled-check.js')('chrome-launcher.js');
var launch = require('./chrome-launcher').launch;
var args = process.argv.slice(2);
var chromeFlags;
var startingUrl;
var port;
var enableExtensions;
if (args.length) {
    chromeFlags = args.filter(function (flag) { return flag.startsWith('--'); });
    var portFlag = chromeFlags.find(function (flag) { return flag.startsWith('--port='); });
    port = portFlag && portFlag.replace('--port=', '');
    enableExtensions = !!chromeFlags.find(function (flag) { return flag === '--enable-extensions'; });
    startingUrl = args.find(function (flag) { return !flag.startsWith('--'); });
}
launch({
    startingUrl: startingUrl,
    port: port,
    enableExtensions: enableExtensions,
    chromeFlags: chromeFlags,
}).then(function (v) { return console.log("\u2728  Chrome debugging port: " + v.port); });
